mapper.update:
update algorithmalarm set state='已确认'

    id in

        #{item}

update algorithmalarm set state='误报'

    id in

        #{item}

mapper.select:
select t1.id,t1.algorithm_id,t1.algorithm_title,t1.border,t2.title,t2.videoa,t2.state,t2.pushaddress, t2.region_id,t2.region_title from algorithm t1 left join camera t2 on t1.camera_id=t2.id
and t1.state='运行中'
and t2.region_id=#{region_id}
and t1.algorithm_id=#{algorithm_id}
and t2.title like concat('%',#{title},'%')

select * from algorithmalarm where state='未确认'

    and algorithm_id= #{algorithm_id}

ORDER BY alarm_time desc
