mapper.update:
update algorithmalarm set state='已确认'

    id in

        #{item}

update algorithmalarm set state='误报'

    id in

        #{item}

mapper.select:
select t1.id,t1.algorithm_id,t1.algorithm_title,t1.border,t2.title,t2.videoa,t2.state,t2.pushaddress, t2.region_id,t2.region_title from (select * from algorithm where algorithm_id=6 and state='运行中') t1 right join camera t2 on t1.camera_id=t2.id
and t2.state='运行中'
and region_id=#{region_id}
and t1.algorithm_id=#{algorithm_id}
and t2.title like concat('%',#{title},'%')

select t1.*,t2.border from algorithmalarm t1,algorithm t2 where t1.algorithm_id=t2.algorithm_id and t1.camera_id = t2.camera_id and t1.state='未确认'
and t1.algorithm_id= #{algorithm_id}
ORDER BY t1.alarm_time desc
